mapper.select:
SELECT s.waybill_number, `fun_round`(s.weight+(${baseWeight}))AS weight, `fun_round`(s.weight) AS old_weight, s.sender AS customer_name, a.send_province, s.branch AS dot_name, a.recipient_province AS destination_province, s.create_date, s.dept_id, (SELECT `code` FROM exp_customer c WHERE s.sender=c.`name` LIMIT 0,1)AS customer_code FROM exp_daily_scan s INNER JOIN exp_balance_account a ON s.waybill_number = a.waybill_number
${filter}
AND s.waybill_number IN (
#{item}
)

SELECT s.waybill_number, `fun_round`(s.weight+(${baseWeight}))AS weight, `fun_round`(s.weight) AS old_weight, t.customer_name, s.branch AS dot_name, t.destination_province AS destination_province, s.create_date, s.dept_id, t.customer_code FROM exp_daily_scan s INNER JOIN exp_temp t ON t.waybill_number = s.waybill_number
${filter}
And DATE_FORMAT(s.create_date,'%Y-%m-%d')=#{dates}

SELECT r.waybill_number, `fun_round`(s.weight+(${baseWeight}))AS weight, `fun_round`(s.weight) AS old_weight, r.customer_name AS customer_name, r.destination_province, r.dot_name AS dot_name, r.create_date, r.customer_code, r.dept_id FROM exp_order_rookie r INNER JOIN exp_daily_scan s ON r.waybill_number = s.waybill_number
${filter}
And DATE_FORMAT(s.create_date,'%Y-%m-%d')=#{dates}

SELECT o.id, o.waybill_number, o.customer_code, ( SELECT p.money FROM exp_price p,exp_customer c where p.price_name =c.price_name AND c.`code` = o.customer_code AND p.weight = o.weight AND p.province_name = o.destination_province LIMIT 0,1 ) AS money, o.create_date, o.customer_name, o.weight, o.old_weight, o.destination_province FROM exp_orders o
${filter}
And DATE_FORMAT(o.create_date,'%Y-%m-%d')=#{dates}

SELECT m.waybill_number, m.create_date, m.money, m.dept_id, m.column_name as des, o.destination_province, o.weight, o.old_weight, o.customer_code, o.customer_name FROM exp_money_in_out m INNER JOIN exp_orders o ON m.waybill_number = o.waybill_number
${filter}
And DATE_FORMAT(m.create_date,'%Y-%m-%d')=#{dates}
AND m.money > 0 AND m.column_name LIKE '支出%'

SELECT m.waybill_number, m.create_date, m.money, m.dept_id, o.destination_province, o.weight, o.old_weight, o.customer_code, o.customer_name, m.column_name as des FROM exp_money_in_out m INNER JOIN exp_orders o ON m.waybill_number = o.waybill_number
${filter}
And DATE_FORMAT(m.create_date,'%Y-%m-%d')=#{dates}
AND m.money > 0 AND m.column_name LIKE '收入%'

SELECT c.`code` AS customer_code, c.`name` AS customer_name, o.dept_id, o.account AS des, money_in AS money, o.create_time AS create_date, d.destination_province, d.weight, d.old_weight, d.waybill_number FROM exp_general_in_out o INNER JOIN exp_customer c ON c.`code` = o.customer_id OR c.`name` = o.consumer LEFT JOIN exp_orders d ON o.waybill_number = d.waybill_number
${filter}
And DATE_FORMAT(o.create_time,'%Y-%m-%d')=#{dates}
GROUP BY o.id

