mapper.select:
SELECT s.waybill_number, `fun_round`(s.weight+(SELECT base_weight FROM exp_base LIMIT 0,1))AS weight, `fun_round`(s.weight) AS old_weight, s.sender AS customer_name, a.send_province, s.branch AS dot_name, a.recipient_province AS destination_province, s.create_date, s.dept_id, (SELECT `code` FROM exp_customer c WHERE s.sender=c.`name` LIMIT 0,1)AS customer_code FROM exp_daily_scan s INNER JOIN exp_balance_account a ON s.waybill_number = a.waybill_number
${filter}
AND s.waybill_number IN (
#{item}
)

SELECT a.waybill_number, `fun_round`(s.weight+(SELECT base_weight FROM exp_base LIMIT 0,1))AS weight, `fun_round`(s.weight) AS old_weight, s.sender AS customer_name, a.send_province, s.branch AS dot_name, a.recipient_province AS destination_province, s.create_date, r.customer_code, a.dept_id FROM exp_balance_account a INNER JOIN exp_order_rookie r ON a.waybill_number = r.waybill_number INNER JOIN exp_daily_scan s ON a.waybill_number = s.waybill_number
${filter}
And DATE_FORMAT(s.create_date,'%Y-%m-%d')=#{dates}

SELECT o.id, o.waybill_number, o.customer_code, ( SELECT p.money FROM exp_price p,exp_customer c where p.price_name =c.price_name AND c.`code` = o.customer_code AND p.weight = o.weight AND p.province_name = o.destination_province LIMIT 0,1 ) * o.weight AS money, o.create_date, o.customer_name, o.weight, o.old_weight, o.destination_province FROM exp_orders o
${filter}
And DATE_FORMAT(o.create_date,'%Y-%m-%d')=#{dates}

SELECT m.waybill_number, m.create_date, m.money, m.dept_id, o.destination_province, o.weight, o.old_weight, o.customer_code, o.customer_name FROM exp_money_in_out m INNER JOIN exp_orders o ON m.waybill_number = o.waybill_number
${filter}
And DATE_FORMAT(o.create_date,'%Y-%m-%d')=#{dates}
AND m.money > 0 AND m.column_name LIKE '支出%'

SELECT m.waybill_number, m.create_date, m.money, m.dept_id, o.destination_province, o.weight, o.old_weight, o.customer_code, o.customer_name FROM exp_money_in_out m INNER JOIN exp_orders o ON m.waybill_number = o.waybill_number
${filter}
And DATE_FORMAT(o.create_date,'%Y-%m-%d')=#{dates}
AND m.money > 0 AND m.column_name LIKE '收入%'

