SELECT v.customer_code, v.customer_name, v.waybill_number, v.destination_dot, v.debtor_weight, v.debtor_money, IFNULL(len.lender_money,0) AS lender_money, v.create_date, ${baseBil} AS base_bil, ( v.debtor_money - IFNULL(len.lender_money,0) - ${baseBil} ) AS gross_profit FROM exp_voucher v LEFT JOIN ( SELECT SUM(le.debtor_money) AS lender_money, le.waybill_number FROM exp_voucher le WHERE le.two_level_coding = '600201' GROUP BY le.waybill_number ) AS len ON len.waybill_number = v.waybill_number
${filter}
AND v.two_level_coding = '112201'
AND DATE_FORMAT(v.create_date, '%Y-%m-%d')>=#{startDates}
AND DATE_FORMAT(v.create_date, '%Y-%m-%d')<=#{endDates}
AND v.debtor_money > 0
LIMIT #{currPage},#{limit}

           SELECT
				count(*)
			FROM
				exp_voucher v


				${filter}

				AND v.two_level_coding = '112201'

				AND DATE_FORMAT(v.create_date, '%Y-%m-%d')>=#{startDates}


				AND DATE_FORMAT(v.create_date, '%Y-%m-%d')<=#{endDates}

				AND v.debtor_money > 0

SELECT count(v.waybill_number) AS count, v.customer_code AS customerCode, v.customer_name AS customerName, v.debtor_weight AS weight, v.destination_dot As destinationDot, sum(v.debtor_money) AS debtorMoney, sum(IFNULL(len.lender_money,0)) AS lenderMoney, ${baseBil} AS baseBil, sum( v.debtor_money - IFNULL(len.lender_money,0)- ${baseBil} ) AS grossProfit FROM exp_voucher v LEFT JOIN ( SELECT SUM(le.debtor_money) AS lender_money, le.waybill_number FROM exp_voucher le WHERE le.two_level_coding = '600201' GROUP BY le.waybill_number ) AS len ON len.waybill_number = v.waybill_number
${filter}
AND v.two_level_coding = '112201' AND v.debtor_money > 0
AND DATE_FORMAT(v.create_date, '%Y-%m-%d')>=#{startDates}
AND DATE_FORMAT(v.create_date, '%Y-%m-%d')<=#{endDates}
AND (v.customer_code=#{customer} OR v.customer_name=#{customer})
AND debtor_weight=#{weight}
AND v.destination_dot=#{province}
GROUP BY v.two_level_coding
,v.customer_code
,v.customer_code
,debtor_weight
,v.destination_dot

SELECT v.customer_name as customerName, v.customer_code as customerCode, temp.type as customerType, SUM(IFNULL(v.debtor_money,0)) AS 'debtorSum', SUM(IFNULL(v.lender_money,0)) AS 'lenderSum', IFNULL(temp.initial_balance,0) AS 'initialBalance', IFNULL(temp.initial_balance,0)+SUM(IFNULL(v.debtor_money,0))-SUM(IFNULL(v.lender_money,0)) AS ' endingBalance' FROM exp_voucher v LEFT JOIN ( SELECT SUM(IFNULL(v.debtor_money, 0)) - SUM(IFNULL(v.lender_money, 0)) AS 'initial_balance', c.type, v.customer_code FROM exp_voucher v LEFT JOIN exp_customer c ON v.customer_code = c.`code`
v.two_level_coding = '112201'
AND DATE_FORMAT(v.create_date, '%Y-%m-%d') <#{start_dates}
GROUP BY v.customer_code ) AS temp ON temp.customer_code = v.customer_code
${filter}
And v.two_level_coding = '112201'
AND DATE_FORMAT(v.create_date, '%Y-%m-%d') >= #{start_dates}
AND DATE_FORMAT(v.create_date, '%Y-%m-%d') <=#{end_dates}
GROUP BY v.customer_code LIMIT #{currPage},#{limit}

            select count(*) from(
				SELECT
				count(*)
				FROM
					exp_voucher v


					${filter}

					AND v.two_level_coding = '112201'

					   AND DATE_FORMAT(v.create_date, '%Y-%m-%d') >= #{start_dates}


						AND DATE_FORMAT(v.create_date, '%Y-%m-%d') <=#{end_dates}

					GROUP BY v.customer_code
					) as x
